export  const ADD = 'ADD';
export  const CLEAR = 'CLEAR';
export  const MARKED = 'MARKED';

export const listAdd = (text) => {
    return { type: ADD, payload: text };
}


export const isaretle  = id =>{
    return { type: 'ISARETLE', payload: id };
};

export const temizle = () =>{
    return { type: 'TEMIZLE'}
}
